import React, { useState } from "react";
import LeftMenu from "./Sections/LeftMenu";
import RightMenu from "./Sections/RightMenu";
import { Drawer, Button, Icon, Badge } from "antd";
import { MenuOutline } from "@ant-design/icons";

import "./Sections/Navbar.css";
function NavBar() {
  const [visible, setVisible] = useState(false);

  const showDrawer = () => {
    setVisible(true);
  };

  const onClose = () => {
    setVisible(false);
  };

  return (
    <nav
      className="menu"
      style={{ position: "fixed", zIndex: 5, width: "100%" }}
    >
      <div className="menu__logo">
        <a href="/">SneakerStore</a>
      </div>
      <div className="menu__container">
        <div className="menu_rigth">
          <RightMenu mode="horizontal" />
        </div>
        <Badge sty>
          <a href="/user/cart" style={{ marginRight: -22, color: "#667777" }}>
            <Icon
              type="shopping-cart"
              style={{ fontSize: 30, marginBottom: 3 }}
            />
          </a>
        </Badge>
        <Button className="menu__mobile-button" onClick={showDrawer}>
          <Icon type="menu" theme="outlined" />
        </Button>
        <Drawer
          placement="right"
          className="menu_drawer"
          closable={false}
          onClose={onClose}
          visible={visible}
        >
          <LeftMenu mode="inline" />
          <RightMenu mode="inline" />
        </Drawer>
      </div>
    </nav>
  );
}

export default NavBar;
